
public class FormulaGeneral {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a, b, c;
		a=1;
		b=-4;
		c=3;
	
			
			double x1;
			double x2;

	        x1 = (-(b) + (Math.sqrt ((b*b - (4*a*c)))))/(2*a) ;
	        x2 = (-(b) - (Math.sqrt ((b*b - (4*a*c)))))/(2*a) ;
	        
	        System.out.println(x1);
	        System.out.println(x2);

		
	} //main
} //class
