
public class Operaciones {
	
	public int sum(int a, int b) {
		return a+b;
	}//suma
	
	public int rest(int a, int b) {
		return a-b;
	}//resta
	
	public int multiplicar(int a, int b) {
		return a*b;
	}//multiplica
	
	public int dividir(int a, int b) {
		return a/b;
	}//divide

	public double getPI() {
		// TODO Auto-generated method stub
		return 3.141592;
	}//getPI
	
}//classOperaciones
